package com.divya_prakash.cliff.viewmodel

import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.divya_prakash.cliff.model.Item
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.delay
import kotlinx.coroutines.launch

class UrlItemsViewModel: ViewModel() {
    private val itemsLiveData =  MutableLiveData<List<Item>>()

    fun getItems(): LiveData<List<Item>> {
        viewModelScope.launch(Dispatchers.IO) {
            // Added this for the testing must be removed in production
            // TODO: 27/06/2021 Must be removed in production
            delay(200)
            val items = MockData.urls.map {
                Item(it)
            }
            itemsLiveData.postValue(items)
        }
        return itemsLiveData
    }
}
